var list = [];
db.getCollection('ref_company').aggregate([
    {$match:{$or:[
        {$and:[
            {list_facebook_work_id:{$ne:[]}},
            {list_facebook_work_id:{$exists:true}},
        ]},
        {$and:[
            {list_facebook_group_id:{$ne:[]}},
            {list_facebook_group_id:{$exists:true}},
        ]},    
    ]}},
    {$lookup:{
            from                : "ref_company_block",
            localField          : "company_block",
            foreignField        : "_id",
            as                  : "blocks",
    }},
    {$project:{_id:1,city_id:1,city_name:1,company_block_id:"$company_block",company_block_name:{$arrayElemAt:["$blocks.name",0]}}},    
]).forEach(function(item){
    if(item.company_block_id && item.company_block_name) {
        list.push("update user_company set company_block_id = " +item.company_block_id + ",company_block_name = '" +item.company_block_name + "' where company_id = " + item._id);
    }
})
print(list.join(";\n"));
